example.code:
//Add an error to the node
IXMLDOMDocument myDOM = thisXDocument.DOM;
IXMLDOMNode myNode = myDOM.selectSingleNode("my:myFields/my:aGroup/my:field1");
thisXDocument.Errors.<span class="label">Add</span>(myNode,"condition","short","detail",102057,"modeless");

for (int i=0; i &lt; thisXDocument.Errors.<span class="label">Count</span>; i++)
{
 thisXDocument.UI.Alert("Error message: " + thisXDocument.Errors[i].ShortErrorMessage);
}

//Delete the errors associated with a specific node
IXMLDOMDocument myDOM = thisXDocument.DOM;
IXMLDOMNode myNode = myDOM.selectSingleNode("my:myFields/my:aGroup/my:field1");
thisXDocument.Errors.<span class="label">Delete</span>(myNode,thisXDocument.Errors[0].ConditionName.ToString());
