[InfoPathEventHandler(EventType=InfoPathEventType.OnMergeRequest)]
public void OnMergeRequest(MergeEvent e)
{ 
 thisXDocument.<span class="label">ImportDOM</span>(e.DOM);
 e.ReturnStatus = true;
}

if (thisXDocument.<span class="label">IsDirty</span>)
{
 thisXDocument.UI.Alert("Data has been changed.");
}
else
{
 thisXDocument.UI.Alert("Data has not been changed.");
}

[InfoPathEventHandler(MatchPath="/my:myFields/my:field1", EventType=InfoPathEventType.OnAfterChange)]
public void field1_OnAfterChange(DataDOMEvent e)
{
 // Determine whether the XML DOM is read-only.
 if (thisXDocument.<span class="label">IsDOMReadOnly</span>)
  return;

 // Continue normal processing...
}

if (thisXDocument.<span class="label">IsNew</span>)
{
 thisXDocument.UI.Alert("Please save your form.");
}

if (thisXDocument.<span class="label">IsReadOnly</span>)
{
 thisXDocument.UI.Alert("The form cannot be modified.");
}

[InfoPathEventHandler(EventType=InfoPathEventType.OnLoad)]
public void OnLoad(DocReturnEvent e)
{
 thisXDocument.UI.Alert("Last saved by an AutoRecover save operation: " + thisXDocument.<span class="label">IsRecovered</span>);
}

if (thisXDocument.<span class="label">IsSigned</span>)
{
 thisXDocument.UI.Alert("This form contains digital signatures.");
}

